import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepButton from '@material-ui/core/StepButton';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Select from 'react-select';
import './Stepper.css'
import { Link } from 'react-router-dom';
import { Spinner } from 'react-bootstrap'



const styles = theme => ({
    root: {
        // width: '60%',
        borderRadius: '10px',
        backgroundColor: 'rgba(20,50,70,0.6)',
        color: 'white!important'
    },
    button: {
        marginRight: theme.spacing.unit,
        color: 'white!important'
    },
    backButton: {
        marginRight: theme.spacing.unit,
    },
    completed: {
        display: 'inline-block',
    },
    instructions: {
        marginTop: theme.spacing.unit,
        marginBottom: theme.spacing.unit,
        color: 'white!important',
    },
    stepper1: {
        borderRadius: '10px',
        backgroundColor: 'transparent !important',
        color: 'white!important',
        iconColor: '#49a942'
    },
    whiteText: {
        color: 'white!important'
    },
    greenBackGround: {
        color: 'white!important',
        icon: '#49a942 !important'

    },
    button: {
        background: '#49a942 !important',
        marginRight: '30px !important;'
    },

    buttonMargin: {
        marginLeft: '30px !important',
        color: 'white!important',
    }


});



function getSteps() {
    return ['Job Industry', 'House price', 'Nationality', 'Order Result By'];
}



class HorizontalNonLinearAlternativeLabelStepper extends React.Component {
    state = {
        activeStep: 0,
        completed: new Set(),
        skipped: new Set(),
        Nationality: "",
        Age: "",
        Job: "",
        RentHouse: "",
        BuyHouse: "",
        getResult: false,
        rentOrBuy: "rent",
        topFiveAreas: null,
        SuggestionClick: false,
        Mostimportant: 'dis'
    };


    JobSelectionChanged = (e) => {
        this.setState({
            Job: e.value
        });
    }

    totalSteps = () => getSteps().length;


    isStepOptional = step => step === 100;

    handleSkip = () => {
        const { activeStep } = this.state;
        if (!this.isStepOptional(activeStep)) {
            // You probably want to guard against something like this
            // it should never occur unless someone's actively trying to break something.
            throw new Error("You can't skip a step that isn't optional.");
        }

        this.setState(state => {
            const skipped = new Set(state.skipped.values());
            skipped.add(activeStep);
            return {
                activeStep: state.activeStep + 1,
                skipped,
            };
        });
    };

    handleNext = () => {
        let activeStep;

        if (this.isLastStep() && !this.allStepsCompleted()) {
            // It's the last step, but not all steps have been completed
            // find the first step that has been completed
            const steps = getSteps();
            activeStep = steps.findIndex((step, i) => !this.state.completed.has(i));
        } else {
            activeStep = this.state.activeStep + 1;
        }
        this.setState({
            activeStep,
        });
    };

    handleBack = () => {
        this.setState(state => ({
            activeStep: state.activeStep - 1,
        }));
    };

    handleStep = step => () => {
        this.setState({
            activeStep: step,
        });
    };

    handleComplete = () => {
        // eslint-disable-next-line react/no-access-state-in-setstate
        const completed = new Set(this.state.completed);
        completed.add(this.state.activeStep);
        this.setState({
            completed,
        });

        /**
         * Sigh... it would be much nicer to replace the following if conditional with
         * `if (!this.allStepsComplete())` however state is not set when we do this,
         * thus we have to resort to not being very DRY.
         */
        if (completed.size !== this.totalSteps() - this.skippedSteps()) {
            this.handleNext();
        }
    };

    handleReset = () => {
        this.setState({
            activeStep: 0,
            completed: new Set(),
            skipped: new Set(),
            getResult: false,
            SuggestionClick: false,
            Nationality: "",
            Age: "",
            Job: "",
            RentHouse: "",
            BuyHouse: "",
            rentOrBuy: "rent",
        });
    };

    skippedSteps() {
        return this.state.skipped.size;
    }

    isStepSkipped(step) {
        return this.state.skipped.has(step);
    }

    isStepComplete(step) {
        return this.state.completed.has(step);
    }

    completedSteps() {
        return this.state.completed.size;
    }

    allStepsCompleted() {
        return this.completedSteps() === this.totalSteps() - this.skippedSteps();
    }

    isLastStep() {
        return this.state.activeStep === this.totalSteps() - 1;
    }

    RentHouseSelectionChanged = (e) => {
        this.setState({
            RentHouse: e.value
        });
    }

    BuyHouseSelectionChanged = (e) => {
        this.setState({
            BuyHouse: e.value
        });
    }

    NationalitySelectionChanged = (e) => {
        this.setState({
            Nationality: e.value
        });
    }

    PrioritySelectionChanged = (e) => {
        this.setState({
            Mostimportant: e.value
        });
    }

    getSuggestionButtonClick = () => {

        const job = this.state.Job
        const nationlity = this.state.Nationality
        const buyHouse = this.state.BuyHouse
        const rentHouse = this.state.RentHouse
        const mostimportant = this.state.Mostimportant
        let count = 0

        if (job === "") {
            count = count + 1
        }
        if (nationlity === "") {
            count = count + 1
        }
        if (buyHouse === "") {
            count = count + 1
        }
        if (buyHouse === "") {
            count = count + 1
        }

        if (count > 3) {
            alert("Fill in more select list to get better suggestion!")
        }
        else {
            let jsonResponse = null
            let url = 'https://scin-model-dev.appspot.com/findreg?nat=' + nationlity +
                '&jin=' + job + '&hpr=' + buyHouse + '&hrt=' + rentHouse + '&sort=' + mostimportant
            var res = encodeURI(url);
            var x = new XMLHttpRequest();
            x.open('GET', res);
            x.onload = x.onerror = function () {
                var data = x.responseText;
                jsonResponse = JSON.parse(data);
                console.log(jsonResponse)
                this.setState({
                    topFiveAreas: jsonResponse,
                    getResult: true
                })
            }.bind(this)
            x.send();
            this.setState({
                SuggestionClick: true
            })
        }

    }

    render() {

        let pStyle = {
            color: 'white',
            marginBottom: '10px',
            margin: '0px',
            marginLeft: '30px'
        }

        let bStyle = {
            background: '#49a942',
            marginTop: '10px'
        }

        let p1Style = {
            color: 'white',
            marginBottom: '10px',
            margin: '0px',
            marginLeft: '30px',
            textAlign: 'left',
            marginTop: '10px',
            marginRight: '10px'

        }

        const compareBtnStyle = {
            marginLeft: '30px',
            backgroundColor: '#49a942 !important',
        }


        const colourStyles = {

            option: base => ({
                ...base,
                color: 'black',
                marginLeft: '30px'
            })

        };
        let result = null
        if (this.state.getResult) {
            result = <div className="suggestion">
                <h2 className='stepperh2'>Top 5 areas that match your preference</h2>
                <div className="top5areas">
                    <p>1. {this.state.topFiveAreas[0].name}</p>
                    <p>2. {this.state.topFiveAreas[1].name}</p>
                    <p>3. {this.state.topFiveAreas[2].name}</p>
                    <p>4. {this.state.topFiveAreas[3].name}</p>
                    <p>5. {this.state.topFiveAreas[4].name}</p></div>
                <Link to={{
                    pathname: '/compare',
                    state: {
                        topFiveAreas: this.state.topFiveAreas,
                        jobIndustry: this.state.Job,
                        culture: this.state.Nationality
                    }
                }}>
                    <Button className='compareFiveButton' variant="contained" color="primary" style={compareBtnStyle}>Compare among Top 5</Button>
                </Link>
                <Button onClick={() => this.setState({ getResult: false, SuggestionClick: false })} style={{ color: 'white' }}>Back</Button>
                <p style={p1Style}>*The result shows the top five regions with profile that is closest matched with your preference input.</p>
            </div>
        }


        const nationlity = [
            {
                "label": "Not concerned",
                "value": ""
            },
            {
                "value": "Afghanistan",
                "label": "Afghanistan"
            },
            {
                "value": "Australia",
                "label": "Australia"
            },
            {
                "value": "Bangladesh",
                "label": "Bangladesh"
            },
            {
                "value": "Bosnia Herzegov",
                "label": "Bosnia Herzegov"
            },
            {
                "value": "Cambodia",
                "label": "Cambodia"
            },
            {
                "value": "Canada",
                "label": "Canada"
            },
            {
                "value": "Chile",
                "label": "Chile"
            },
            {
                "value": "China",
                "label": "China"
            },
            {
                "value": "Croatia",
                "label": "Croatia"
            },
            {
                "value": "Egypt",
                "label": "Egypt"
            },
            {
                "value": "England",
                "label": "England"
            },
            {
                "value": "Fiji",
                "label": "Fiji"
            },
            {
                "value": "France",
                "label": "France"
            },
            {
                "value": "Germany",
                "label": "Germany"
            },
            {
                "value": "Greece",
                "label": "Greece"
            },
            {
                "value": "Hong Kong",
                "label": "Hong Kong"
            },
            {
                "value": "India",
                "label": "India"
            },
            {
                "value": "Indonesia",
                "label": "Indonesia"
            },
            {
                "value": "Iran",
                "label": "Iran"
            },
            {
                "value": "Iraq",
                "label": "Iraq"
            },
            {
                "value": "Ireland",
                "label": "Ireland"
            },
            {
                "value": "Italy",
                "label": "Italy"
            },
            {
